import React, { Component } from 'react'
import axios from 'axios';
import { Route , withRouter,NavLink} from 'react-router-dom';
import StudentHome from './Student/StudentHome';
import Input from './components/input/input'

class Login extends Component{
    constructor(props){
        super();
        this.state ={
            email:"",
            emailt:"",
            pass:"",
            passt:""
        };
}

    StudentHandler=(e)=>{

        axios.post("http://localhost:8081/login", {email: this.state.email, pass: this.state.pass})
        .then((res)=>{
            if (res.data.email == this.state.email){
                localStorage.setItem("usertype", "student")
                localStorage.setItem("token", res.data.token)
                window.location.reload();
            }
        })

    }

    AdminHandler=(e)=>{

        axios.post("http://localhost:8081/Adminlogin", {email: this.state.emailt, pass: this.state.passt})
        .then((res)=>{
            console.log (res)
            if (res.data.email == this.state.emailt){
                if (res.data.isadmin == 0){
                    localStorage.setItem("usertype", "teacher")
                    alert("hello")
                }
                else{
                    localStorage.setItem("usertype", "bigteacher")
                    alert("hello lsjdflksdjfl")
                }
                localStorage.setItem("token", res.data.token)
                window.location.reload();
            }
        })

    }
    render(){
        return(
            <div>
            <div className = "stdlogin">
                StudentLogin
                <br></br>
                email
                <Input
                inputSize="input"
                 type = "text" 
                value={this.state.email}
                changed={e=>this.setState({email:e.target.value})}
                />

                <br></br>

                Pass
                <Input 
                 inputSize="input"
                 type = "password" 
                 value={this.state.pass}

                changed={e=>this.setState({pass:e.target.value})}/>

                <button onClick={this.StudentHandler} >Student Login</button>
            </div>

            <br></br>

            <div className = "Adminlogin">
                Librarian Log in
                <br></br>
                Name
                <Input
                inputSize="input"
                 type = "text" 
                value={this.state.emailt}
                changed={e=>this.setState({emailt:e.target.value})}
                />

                <br></br>

                Pass
                <Input 
                 inputSize="input"
                 type = "password" 
                 value={this.state.passt}

                changed={e=>this.setState({passt:e.target.value})}/>

                <button onClick={this.AdminHandler} >Admin Login</button>
            </div>
            </div>
        )
    }
}
export default Login;
